function showPopup(){$('embed, object, select').css({'visibility':'hidden'});$('body').append('<div id="popupWnd"></div><div id="popupMsg"></div>');$('#popupWnd').css({'width':$(document).width(),'height':$(document).height(),'position':'absolute','left':'0px','top':'0px','opacity':0.8,'background-color':'#000','z-index':5999}).fadeIn();$('#popupWnd').click(function(){hidePopup();});$('#popupMsg').css({'position':(($.browser.msie)?'absolute':'fixed'),'left':($(document).width()/2-400)+'px','top':(($.browser.msie)?60:($(window).height()/2-300))+'px','width':'800px','height':'600px','overflow':'hidden','z-index':6000}).html('<div id="popupCnt"></div><div id="popupLdg"></div>').show();$('#popupCnt').css({'width':'800px','height':'600px'});$('#popupLdg').css({'width':'800px','height':'600px','display':'none'}).html('<img src="/public/images/ajax.gif" style="width:32px;height:32px;border:0 none;margin:284px 0 0 384px" alt="Ожидаем..." title="Ожидаем...">');}function hidePopup(){$('#popupMsg').remove();$('#popupWnd').fadeOut(function(){$(this).remove();});$('embed, object, select').css({'visibility':'visible'});}
